sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
  var size = g_form.getValue('size');

    switch(size) {
        case 'big':
            g_form.setValue('weight_capacity', '1600LBS');
			g_form.setValue('price', '13' ); 
            break;
        case 'med':
            g_form.setValue('weight_capacity', '800LBS'); 
			g_form.setValue('price', '14' );  
            break;
        case 'small':
            g_form.setValue('weight_capacity', '200LBS'); 
			g_form.setValue('price', '15' );  
            break;
        default:
            g_form.setValue('weight_capacity', ''); 
    }
}
